import React, { useEffect, useState } from "react"
import axios from "axios"
import { useNavigate, Link } from "react-router-dom"

function Login() {
    const history = useNavigate();
    const [email, setEmail] = useState('')
    const [password, setPassword] = useState('')

    async function submit(e) {
        e.preventDefault();

        try {
            await axios.post("http://localhost:5000/", {
                email, password
            })
            .then(res => {
                if (res.data === "exist") {
                    history("/home", { state: { id: email } })
                } else if (res.data === "notexist") {
                    alert("User has not signed up")
                }
            })
            .catch(e => {
                alert("Wrong details")
                console.log(e);
            })

        } catch (e) {
            console.log(e);
        }
    }

    const formStyle = {
        width: "300px",
        margin: "auto",
        textAlign: "center",
        border: "1px solid #ccc",
        padding: "20px",
        borderRadius: "5px",
        marginTop: "50px"
    };

    const inputStyle = {
        width: "100%",
        marginBottom: "10px",
        padding: "8px"
    };

    const submitButtonStyle = {
        width: "100%",
        padding: "10px",
        background: "#007bff",
        color: "#fff",
        border: "none",
        borderRadius: "5px",
        cursor: "pointer"
    };

    const linkStyle = {
        textDecoration: "none",
        color: "#007bff"
    };

    return (
        <div className="login">
            <h1>Login</h1>
            <form action="POST" style={formStyle}>
                <input type="email" onChange={(e) => { setEmail(e.target.value) }} placeholder="Email" style={inputStyle} />
                <input type="password" onChange={(e) => { setPassword(e.target.value) }} placeholder="Password" style={inputStyle} />
                <input type="submit" onClick={submit} style={submitButtonStyle} />
            </form>
            <br />
            <p>OR</p>
            <br />
            <Link to="/signup" style={linkStyle}>Signup Page</Link>
        </div>
    )
}

export default Login
