import React, { useState } from "react";
import axios from "axios";
import { useNavigate, Link } from "react-router-dom";

function Login() {
  const history = useNavigate();

  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');

  async function submit(e) {
    e.preventDefault();

    try {
      await axios.post("http://localhost:5000/", {
        email,
        password,
      })
        .then((res) => {
          if (res.data === "exist") {
            history("/home", { state: { id: email } });
          } else if (res.data === "notexist") {
            alert("User has not signed up");
          }
        })
        .catch((e) => {
          alert("Wrong details");
          console.log(e);
        });
    } catch (e) {
      console.log(e);
    }
  }

  return (
    <div className="login" style={styles.container}>
      <h1 style={styles.header}>Login</h1>

      <form action="POST">
        <input
          type="email"
          onChange={(e) => {
            setEmail(e.target.value);
          }}
          placeholder="Email"
          style={styles.input}
        />
        <input
          type="password"
          onChange={(e) => {
            setPassword(e.target.value);
          }}
          placeholder="Password"
          style={styles.input}
        />
        <input type="submit" onClick={submit} style={styles.submitButton} />
      </form>

      <br />
      <p style={styles.orText}>OR</p>
      <br />

      <Link to="/signup" style={styles.link}>Signup Page</Link>
    </div>
  );
}

const styles = {
  container: {
    textAlign: "center",
    margin: "auto",
    width: "50%",
    marginTop: "100px",
    fontFamily: "Arial, sans-serif",
    backgroundColor: "#333",
    color: "#fff",
    padding: "30px",
    borderRadius: "8px",
  },
  header: {
    color: "#ffcc00",
    fontSize: "2em",
  },
  input: {
    width: "80%",
    padding: "10px",
    margin: "10px",
    borderRadius: "5px",
    border: "1px solid #ccc",
    backgroundColor: "#444",
    color: "#fff",
  },
  submitButton: {
    width: "40%",
    padding: "10px",
    margin: "10px",
    borderRadius: "5px",
    border: "none",
    backgroundColor: "#ffcc00",
    color: "#333",
    cursor: "pointer",
  },
  orText: {
    fontSize: "1.2em",
    color: "#ffcc00",
  },
  link: {
    textDecoration: "none",
    color: "#ffcc00",
    fontSize: "1.1em",
  },
};

export default Login;
